import { useCart } from '../../hooks/CartContext';
import { useNavigate } from 'react-router-dom';
import { CheckoutContainer,CheckoutBackground, CheckoutResponsive} from '../../components/ui/checkout';

function CheckoutPage() {
  const { cart, clearCart } = useCart();
  const navigate = useNavigate();

  function handleCheckout() {
    clearCart();
    navigate('/CheckoutSuccess');
  }

  return (
    <CheckoutContainer>
      <CheckoutBackground>
        <h2 className="text-3xl font-extrabold text-gray-800 text-center">Checkout</h2>
        <CheckoutResponsive>
          <div className="lg:col-span-2">
            <h3 className="text-lg font-bold text-gray-800">Choose your payment method</h3>
            <div className="grid gap-4 sm:grid-cols-2 mt-4">
              <div class="flex items-center">
                  <input type="radio" class="w-5 h-5 cursor-pointer" id="card" checked />
                  <label htmlFor="card" class="ml-4 flex gap-2 cursor-pointer">
                    <img src="https://readymadeui.com/images/visa.webp" class="w-12" alt="card1" />
                    <img src="https://readymadeui.com/images/american-express.webp" class="w-12" alt="card2" />
                    <img src="https://readymadeui.com/images/master.webp" class="w-12" alt="card3" />
                  </label>
                </div>
            </div>
          </div>
          <button onClick={handleCheckout}>Checkout</button>
        </CheckoutResponsive>
      </CheckoutBackground>
    </ CheckoutContainer>
  );
}

export default CheckoutPage;
