import { useCart } from "../../hooks/CartContext";
import {
  CheckoutBackground,
  CheckoutContainer,
  CheckoutResponsive,
} from "../../components/ui/checkout";

import { Button } from "../../components/ui/button";
import {
  PriceTagForCartPage,
  PriceTagTotalForCartPage,
} from "../../components/ui/PriceTag";

function CartPage() {
  const { cart, removeFromCart, clearCart, updateCartQuantity } = useCart();

  const handleQuantityChange = (itemId, newQuantity) => {
    // Ensure the quantity is a valid number before updating the cart
    const validQuantity =
      isNaN(newQuantity) || newQuantity < 1 ? 1 : newQuantity;
      updateCartQuantity(itemId, validQuantity);
  };

  const getTotalPrice = (price, discountedPrice, quantity) => {
    return (discountedPrice || price) * quantity;
  };

  return (
    <CheckoutBackground>
      <CheckoutContainer>
        <h1 className="text-2xl font-semibold mb-4">Shopping Cart</h1>
        <CheckoutResponsive>
          <div className="md:w-3/4">
            <div className="bg-white rounded-lg shadow-md p-6 mb-4">
              <table className="w-full">
                <thead>
                  <tr>
                    <th className="text-left font-semibold">Product</th>
                    <th className="text-left font-semibold">Price</th>
                    <th className="text-left font-semibold">Quantity</th>
                    <th className="text-left font-semibold">Total</th>
                  </tr>
                </thead>
                <tbody>
                  {cart?.length > 0 ? (
                    cart.map((item) => {
                      const price = parseFloat(item.data.price) || 0; // Ensure price is a valid number
                      const discountedPrice =
                        parseFloat(item.data.discountedPrice) || 0; // Ensure discounted price is valid
                      const quantity =
                        item.quantity && !isNaN(item.quantity)
                          ? item.quantity
                          : 1; // Ensure quantity is valid, fallback to 1 if invalid

                      const total = getTotalPrice(price, discountedPrice, quantity);

                      return (
                        <tr key={item.id} className="border-t">
                          <td className="py-4">
                            <div className="flex items-center">
                              <img
                                src={item.data.image?.url}
                                alt={item.data.image?.alt}
                                className="h-16 w-16 mr-4"
                              />
                              <span className="font-semibold">
                                {item.data.title}
                              </span>
                            </div>
                          </td>
                          <PriceTagForCartPage
                            price={item.data.price}
                            discountedPrice={item.data.discountedPrice}
                            quantity={quantity}
                          />
                          <td className="py-4">
                            <div className="flex items-center">
                              <button
                                className="border rounded-md py-2 px-4 mr-2"
                                onClick={() =>
                                  handleQuantityChange(item.id, quantity - 1)
                                }
                                disabled={quantity <= 1}
                              >
                                -
                              </button>
                              <span className="text-center w-8">{quantity}</span>
                              <button
                                className="border rounded-md py-2 px-4 ml-2"
                                onClick={() =>
                                  handleQuantityChange(item.id, quantity + 1)
                                }
                              >
                                +
                              </button>
                            </div>
                          </td>
                          <PriceTagTotalForCartPage
                            price={price}
                            discountedPrice={discountedPrice}
                            quantity={quantity} // Pass quantity to calculate total correctly
                          />
                          <td className="py-4">
                            <Button
                              variant="destructive"
                              onClick={() => removeFromCart(item.id)}
                            >
                              Remove
                            </Button>
                          </td>
                        </tr>
                      );
                    })
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center py-4">
                        Your cart is empty.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>

          <div className="md:w-1/4">
            {cart.length > 0 && (
              <Button variant="destructive" onClick={clearCart}>
                Clear Cart
              </Button>
            )}
          </div>
        </CheckoutResponsive>
      </CheckoutContainer>
    </CheckoutBackground>
  );
}

export default CartPage;
