import React, { useState, useEffect } from "react";
import {
  ContactContainer,
  ContactResponsive,
} from "../../components/ui/contactForm";
import { useForm } from 'react-hook-form';

const ContactForm = () => {
  const { register, handleSubmit, reset, formState: { errors } } = useForm();

  const [isFormValid, setIsFormValid] = useState(false);
  const [touched, setTouched] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const onSubmit = (data) => {
    reset();
    setSuccessMessage("Your message has been sent successfully!");
  };

  useEffect(() => {
    validateForm();
  }, [touched]);

  const validateForm = () => {
    let newErrors = {};
    if (typeof touched.body === 'string') {
      
      touched.body.trim();
    }
    // Validation checks for empty fields
    if (touched.fullName && !/\S+/.test(touched.fullName)) {
      newErrors.fullName = "Name is required.";
    }
    if (touched.email && !/\S+@\S+\.\S+/.test(touched.email)) {
      newErrors.email = "Please enter a valid email address.";
    }
    if (touched.subject && touched.subject.trim().length < 3) {
      newErrors.subject = "Subject must be at least 3 characters long.";
    }
    if (touched.body && touched.body.trim().length < 3) {
      newErrors.body = "Message must be at least 3 characters long.";
    }

    setIsFormValid(Object.keys(newErrors).length === 0);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTouched((prevTouched) => ({ ...prevTouched, [name]: value }));
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched((prevTouched) => ({ ...prevTouched, [name]: true }));
    validateForm();
  };

  return (
    <ContactContainer>
      <ContactResponsive>
        <div className="flex-basis-1/2 ">
          <h1 className="text-black text-3xl font-extrabold">Let's Talk</h1>
          <div className="contact-page">
            <h1 className="text-black font-semibold">Contact Us</h1>
            <p className="text-sm text-black mt-4">
              If you can’t find the information you need in our Help articles,
              and would like to get in touch with us, we’re here to help..
            </p>
            <div className="mt-12">
              <h2 className="text-gray-800 text-base font-bold">Email</h2>
              <ul className="mt-4">
                <li className="flex items-center">
                  <div className="bg-[#e6e6e6cf] h-10 w-10 rounded-full flex items-center justify-center shrink-0">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="20px"
                      height="20px"
                      fill="#007bff"
                      viewBox="0 0 479.058 479.058"
                    >
                      <path
                        d="M434.146 59.882H44.912C20.146 59.882 0 80.028 0 104.794v269.47c0 24.766 20.146 44.912 44.912 44.912h389.234c24.766 0 44.912-20.146 44.912-44.912v-269.47c0-24.766-20.146-44.912-44.912-44.912zm0 29.941c2.034 0 3.969.422 5.738 1.159L239.529 264.631 39.173 90.982a14.902 14.902 0 0 1 5.738-1.159zm0 299.411H44.912c-8.26 0-14.971-6.71-14.971-14.971V122.615l199.778 173.141c2.822 2.441 6.316 3.655 9.81 3.655s6.988-1.213 9.81-3.655l199.778-173.141v251.649c-.001 8.26-6.711 14.97-14.971 14.97z"
                      />
                    </svg>
                  </div>
                  <a className="text-blue-900 text-sm ml-4">
                    <small className="block font-semibold">Mail</small>
                    <strong>natnoppol@gmail.com</strong>
                  </a>
                </li>
              </ul>
            </div>
            <div className="mt-12">
              <h2 className="text-gray-800 text-base font-bold">Socials</h2>

              <ul className="flex mt-4 space-x-4">
                {/* Add icons for social media */}
              </ul>
            </div>
          </div>
        </div>

        <form className="flex-basis-1/2 ml-auto space-y-4" onSubmit={handleSubmit(onSubmit)}>
          <input
            type="text"
            id="fullName"
            name="fullName"
            placeholder="Name"
            {...register("fullName", { required: "Name is required." })}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
            aria-label="Full Name"
          />
          {errors.fullName && <p className="error">{errors.fullName.message}</p>}
          <input
            type="text"
            id="subject"
            name="subject"
            placeholder="Subject"
            {...register("subject", { required: "Subject is required." })}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
            aria-label="Subject"
          />
          {errors.subject && <p className="error">{errors.subject.message}</p>}
          <input
            type="email"
            id="email"
            name="email"
            placeholder="Email"
            {...register("email", { required: "Email is required." })}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
            aria-label="Email"
          />
          {errors.email && <p className="error">{errors.email.message}</p>}
          <textarea
            id="body"
            name="body"
            placeholder="Message"
            {...register("body", { required: "Message is required." })}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md px-4 bg-gray-100 text-gray-800 text-sm pt-3 outline-blue-500 focus:bg-transparent"
            rows="6"
            aria-label="Message"
          />
          {errors.body && <p className="error">{errors.body.message}</p>}

          <button
            type="submit"
            disabled={isSubmitting || !isFormValid}
            className="text-white bg-blue-600 hover:bg-blue-700 tracking-wide rounded-md text-sm px-4 py-3 w-full mt-6"
          >
            {isSubmitting ? "Submitting..." : "Submit"}
          </button>

          {successMessage && (
            <p className="text-green-600 font-semibold">{successMessage}</p>
          )}
        </form>
      </ContactResponsive>
    </ContactContainer>
  );
};

export default ContactForm;
