import { createContext, useState, useContext, useEffect } from "react";

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem("cart");
    return savedCart ? JSON.parse(savedCart) : [];
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.data.id === product.data.id);
      let newCart;

      if (existingItem) {
        newCart = prevCart.map((item) =>
          item.data.id === product.data.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        newCart = [...prevCart, { data: product.data, quantity: 1 }];
      }

      localStorage.setItem("cart", JSON.stringify(newCart));
      return newCart;
    });
  };

  const removeFromCart = (productId) => {
    setCart((prevCart) => {
      const updatedCart = prevCart.filter((item) => item.data.id !== productId);
      localStorage.setItem("cart", JSON.stringify(updatedCart)); // 🔥 Ensure localStorage is updated
      return updatedCart;
    });
  };

  const updateCartQuantity = (itemId, newQuantity) => {
    setCart((prevCart) => {
      const updatedCart = prevCart.map((item) =>
        item.data.id === itemId
          ? { ...item, quantity: newQuantity }
          : item
      );
      localStorage.setItem("cart", JSON.stringify(updatedCart)); // 🔥 Ensure localStorage is updated
      return updatedCart;
    });
  };

  const clearCart = () => {
    setCart([]); 
    localStorage.removeItem("cart"); // 🔥 Clear cart from localStorage
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, updateCartQuantity, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
