import React from "react";
import { Link } from "react-router-dom";

const FilteredProducts = ({ filteredProducts, searchTerm, allProducts }) => {
  // แสดงสินค้าทั้งหมดเมื่อไม่มีคำค้นหา, หรือแสดงสินค้าที่กรองแล้วเมื่อมีคำค้นหา
  const displayProducts = searchTerm ? filteredProducts : [];

  return (
    <>
      {/* แสดงผลลัพธ์การค้นหา */}
      {searchTerm && displayProducts.length > 0 && (
        <div className="search-results absolute mt-2 w-full bg-white shadow-lg rounded-md z-10">
          <ul>
            {displayProducts.map((product) => (
              <li key={product.id} className="p-2 hover:bg-gray-200">
                <Link to={`/product/${product.id}`} onClick={() => window.scrollTo(0, 0)}>
                  {product.title}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* แสดงข้อความเมื่อไม่มีผลลัพธ์ */}
      {searchTerm && displayProducts.length === 0 && (
        <div className="no-results mt-2 text-gray-500">
          <p>No results found for "{searchTerm}"</p>
        </div>
      )}
    </>
  );
};

export default FilteredProducts;
