import { useCart } from "../../hooks/CartContext";
import {
  CartBackground,
  CartLeftContainer,
  CartResponsive,
} from "../../components/ui/cart";
import {
  PriceTagForCartPage,
  PriceTagTotalForCartPage,
} from "../../components/ui/priceTag";
import { Link, useNavigate } from "react-router-dom";

function CartPage() {
  const { cart, removeFromCart, updateCartQuantity } = useCart();
  const navigate = useNavigate();

  const subtotal = cart.reduce((sum, item) => {
    const priceToUse = item.data.price - item.data.discountedPrice;
    return sum + priceToUse * item.quantity;
  }, 0);
  const total = subtotal;

  const handleQuantityChange = (itemId, newQuantity) => {
    const validQuantity = Math.max(1, parseInt(newQuantity, 10) || 1);
    updateCartQuantity(itemId, validQuantity);
  };

  function handleCart() {
    navigate("/checkout");
  }

  return (
    <section className="bg-white py-8 antialiased dark:bg-gray-900 md:py-16">
      <div className="mx-auto max-w-screen-xl px-4 2xl:px-0">
        <h2 className="text-xl font-semibold text-gray-900 dark:text-white sm:text-2xl">
          Shopping Cart
        </h2>
        <CartLeftContainer>
          <div className="mx-auto w-full flex-none lg:max-w-2xl xl:max-w-4xl">
            <div className="space-y-6">
              <div className="rounded-lg border border-gray-200 bg-white p-4 shadow-sm dark:border-gray-700 dark:bg-gray-800 md:p-6">
                {cart?.length > 0 ? (
                  cart.map((item, index) => {
                    const price = parseFloat(item.data.price) || 0;
                    const discountedPrice =
                      parseFloat(item.data.discountedPrice) || 0;
                    const quantity =
                      item.quantity && !isNaN(item.quantity) ? item.quantity : 1;

                    return (
                      <div
                        key={`${item.data.id}-${index}`}
                        className="flex items-center justify-between gap-6 border-b pb-4"
                      >
                        {/* Product Image */}
                        <Link
                          to={`/product/${item.data.id}`}
                          className="shrink-0 md:order-1"
                        >
                          <img
                            src={item.data.image?.url}
                            alt={item.data.image?.alt}
                            className="h-20 w-20 dark:hidden"
                          />
                        </Link>
                        <label htmlFor="counter-input" className="sr-only">Choose quantity:</label>
                        {/* Quantity Controls */}
                        <div className="flex items-center justify-between md:order-3 md:justify-end">
                          <div className="flex items-center">
                            <button type="button" id="decrement-button" data-input-counter-decrement="counter-input" 
                              className="inline-flex h-5 w-5 shrink-0 items-center justify-center rounded-md border border-gray-300 bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-100 dark:border-gray-600 dark:bg-gray-700 dark:hover:bg-gray-600 dark:focus:ring-gray-700"
                              onClick={() =>
                                handleQuantityChange(item.data.id, quantity - 1)
                              }
                              disabled={quantity <= 1}>
                              
                              <svg className="h-2.5 w-2.5 text-gray-900 dark:text-white" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 18 2">
                                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 1h16" />
                              </svg>
                            </button>
                            <input type="text" id="counter-input" data-input-counter className="w-10 shrink-0 border-0 bg-transparent text-center text-sm font-medium text-gray-900 focus:outline-none focus:ring-0 dark:text-white" placeholder="" value="2" required />
                          </div>
                          <input
                            type="number"
                            className="w-10 text-center border-0 bg-transparent text-sm font-medium text-gray-900 focus:outline-none dark:text-white"
                            value={quantity}
                            onChange={(e) =>
                              handleQuantityChange(item.data.id, e.target.value)
                            }
                          />
                          <button
                            type="button"
                            className="inline-flex h-5 w-5 items-center justify-center rounded-md border border-gray-300 bg-gray-100 hover:bg-gray-200 dark:border-gray-600 dark:bg-gray-700 dark:hover:bg-gray-600"
                            onClick={() =>
                              handleQuantityChange(item.data.id, quantity + 1)
                            }
                          >
                            <svg
                              className="h-2.5 w-2.5 text-gray-900 dark:text-white"
                              xmlns="http://www.w3.org/2000/svg"
                              fill="none"
                              viewBox="0 0 18 18"
                            >
                              <path
                                stroke="currentColor"
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M9 1v16M1 9h16"
                              />
                            </svg>
                          </button>
                        </div>
                      </div>
                    );
                  })
                ) : (
                  <p className="text-center py-8 text-xl text-gray-600">
                    Your cart is empty.
                  </p>
                )}
              </div>
            </div>
          </div>
        </CartLeftContainer>
      </div>
    </section>
  );
}

export default CartPage;
