import { useCart } from '../../hooks/CartContext';
import { CheckoutBackground, CheckoutContainer, CheckoutResponsive} from '../../components/ui/checkout';

import { Button } from '../../components/ui/button';


function CartPage() {
  const { cart, removeFromCart, clearCart } = useCart();
  
  console.log(cart)

  return (
    <CheckoutBackground>
    <CheckoutContainer>
    <h1 className="text-2xl font-semibold mb-4">Shopping Cart</h1>
    <CheckoutResponsive>
      {cart.data.map((item) => (
        <div key={item.id}>
          {item.image?.url}
          <h2>{item.title}</h2>
          <Button variant="destructive" onClick={() => removeFromCart(item.id)}>Remove</Button>
        </div>
      ))}
      {cart.length > 0 && (
        <Button variant="destructive" onClick={clearCart}>Clear Cart</Button>
      )}

    </CheckoutResponsive>
    </CheckoutContainer>
  </CheckoutBackground>



  );
}

export default CartPage;
