import { useCart } from '../../hooks/CartContext';
import { CheckoutBackground, CheckoutContainer, CheckoutResponsive} from '../../components/ui/checkout';

import { Button } from '../../components/ui/button';


function CartPage() {
  const { cart, removeFromCart, clearCart } = useCart();
  
  console.log(cart)

  return (
    <CheckoutBackground>
    <CheckoutContainer>
      <h1 className="text-2xl font-semibold mb-4">Shopping Cart</h1>
      <CheckoutResponsive>
        <div className="md:w-3/4">
          <div className="bg-white rounded-lg shadow-md p-6 mb-4">
            <table className="w-full">
            <thead>
              <tr>
                <th className="text-left font-semibold">Product</th>
                <th className="text-left font-semibold">Price</th>
                <th className="text-left font-semibold">Quantity</th>
                <th className="text-left font-semibold">Total</th>
                </tr>
            </thead>
            <tbody>
                            <tr>
                                
              </tr>
                           
            </tbody>


                {cart.map((item) => (
                  <div key={item.id}>
                    {item.image?.url}
                    <h2>{item.title}</h2>
                    <Button variant="destructive" onClick={() => removeFromCart(item.id)}>Remove</Button>
                  </div>
                ))}
                {cart.length > 0 && (
                  <Button variant="destructive" onClick={clearCart}>Clear Cart</Button>
                )}
            </table>
          </div>
        </div>

        <div className="md:w-1/4"></div>
      </CheckoutResponsive>
    </CheckoutContainer>
  </CheckoutBackground>



  );
}

export default CartPage;
