        <form className="ml-auto space-y-4" onSubmit={handleSubmit}>
          <input
            type="text"
            id="fullName"
            name="fullName"
            placeholder="Name"
            value={formData.fullName}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
            aria-label="Full Name"
          />
          {errors.fullName && <p className="error">{errors.fullName}</p>}
          <input
            type="text"
            id="subject"
            name="subject"
            placeholder="subject"
            value={formData.subject}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
            aria-label="Subject"
          />
          {errors.subject && <p className="error">{errors.subject}</p>}
          <input
            type="email"
            id="email"
            name="email"
            placeholder="email"
            value={formData.email}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
            aria-label="Email"
          />
          {errors.email && <p className="error">{errors.email}</p>}
          <textarea
            id="body"
            name="body"
            placeholder="Message"
            value={formData.body}
            onChange={handleChange}
            onBlur={handleBlur}
            className="w-full rounded-md px-4 bg-gray-100 text-gray-800 text-sm pt-3 outline-blue-500 focus:bg-transparent"
            rows="6"
            aria-label="Message"
          />
          {errors.body && <p className="error">{errors.body}</p>}

          <button
            type="submit"
            disabled={
              isSubmitting ||
              !formData.fullName ||
              !formData.email ||
              !formData.subject ||
              !formData.body ||
              !isFormValid
            }
            className={`text-white bg-blue-600 hover:bg-blue-700 tracking-wide rounded-md text-sm px-4 py-3 w-full !mt-6 ${
              !isFormValid ||
              !formData.fullName ||
              !formData.email ||
              !formData.subject ||
              !formData.body
                ? "opacity-50 cursor-not-allowed"
                : ""
            }`}
          >
            {isSubmitting ? "Submitting..." : "Submit"}
          </button>

          {successMessage && (
            <p className="text-green-600 font-semibold">{successMessage}</p>
          )}
        </form>
      </ContactResponsive>
      </div>
    </ContactContainer>
  );
};

