import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../../hooks/CartContext';
import { Button, ButtonForSingleProductPage } from '../../components/ui/button';
import { PriceTag, PriceTagForSingleProduct } from '../../components/ui/priceTag';
import {
  SingleProductContainer,
  SingleProductResponsive,
} from '../../components/ui/singleProduct'

function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { addToCart } = useCart();
  const [addedToCart, setAddedToCart] = useState(false);

 
  // Async function to fetch the product data
  const fetchProduct = async () => {
    try {
      const res = await fetch(`https://v2.api.noroff.dev/online-shop/${id}`);
      const data = await res.json();
      setProduct(data);
    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

  // Call the fetchProduct function when the component mounts
  useEffect(() => {
    fetchProduct();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const handleAddToCart = () => {
    addToCart(product);
    setAddedToCart(true);

    setTimeout(() => {
      setAddedToCart(false);
    }, 2000);
  }

 
  return (
    <SingleProductContainer>
      <SingleProductResponsive>
        <div className='space-y-4'>
          <div className="aspect-square bg-gray-100 dark:bg-gray-800 
          rounded-xl overflow-hidden">
          <img
            src={product.data.image?.url || '/default-image.jpg'}
            alt={product.data.image?.alt || 'Product Image'}
            className='w-full h-full object-cover'
          />
          </div>
        </div>

          <div className='space-y-6'>
            <div className="flex items-start justify-between">
              <div>
                <h1 className="text-3xl font-bold mb-2">{product.data.title}</h1>
              </div>
              
              <PriceTagForSingleProduct price= {product.data.price} discountedPrice={product.data.discountedPrice}/>
          </div>
          <div className='text-lg font-semibold'>{product.data.description}</div>
          <div className="flex gap-4">
            <button className="flex-1 bg-sky-800 text-white py-4 rounded-xl hover:bg-sky-800/90" 
            onClick={handleAddToCart}>
              {addedToCart ? "Added!" : "Add to cart"}
            </button>
          </div>
        </div>
      </SingleProductResponsive>
    </SingleProductContainer>
      
  );
}

export default ProductPage;
