import { Link } from "react-router-dom";
import { CartProvider } from "../../hooks/CartContext";
import CartIcon from "../../components/icon/CartIcon";
import {
  FooterContainer,
  FooterResonsive,
  FooterMenu,
} from "../ui/footer";

function Footer() {
  return (
    <FooterContainer className="bg-gray-800 text-white py-4">
      <FooterResonsive className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <FooterMenu className="flex justify-between items-center">
          <div className="flex space-x-6">
            <Link to="/" className="hover:text-primary-500 transition">
              Home
            </Link>
            <Link to="/contact" className="hover:text-primary-500 transition">
              Contact
            </Link>
          </div>
          <CartProvider>
            <CartIcon />
          </CartProvider>
          <div className="text-sm">&copy; 2024 My Ecom Store</div>
        </FooterMenu>
      </FooterResonsive>
    </FooterContainer>
  );
}

export default Footer;
