import { useState, useEffect, useCallback } from "react";
import { Link } from "react-router-dom";
import CartIcon from "../../components/icon/CartIcon";
import {
  NavigationContainer,
  NavigationResonsive,
  NavigationMenu,
  NavigationLogo,
  NavigationMobile,
} from "../ui/navigation-menu";


function Header() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State to toggle the hamburger menu
  
useEffect(() => {
  if (!isMenuOpen) {
    document.getElementById("hamburger-menu-button")?.focus();
  }
}, [isMenuOpen]);

const toggleDarkMode = useCallback(() => {
  setIsDarkMode((prev) => !prev);
  document.documentElement.classList.toggle("dark");
}, []);

const toggleMenu = useCallback(() => {
  setIsMenuOpen((prev) => !prev);
}, []);

useEffect(() => {
  document.getElementById("search-input")?.focus();
}, []);

  // Close menu when a menu item is clicked
  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  // Close menu when Escape key is pressed
  useEffect(() => {
    const handleEscapeKey = (event) => {
      if (event.key === "Escape") {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener("keydown", handleEscapeKey);

    return () => {
      document.removeEventListener("keydown", handleEscapeKey);
    };
  }, []);

  return (
    <NavigationContainer>
      <NavigationResonsive>
        <NavigationLogo>
          <Link
            to="/"
            className="text-primary-600 hover:text-primary-500 transition-colors self-center text-2xl font-bold whitespace-nowrap dark:text-white"
          >
            ShopHub
          </Link>
        </NavigationLogo>

        <NavigationMenu>
          <Link
            to="/"
            className="block font-semibold py-2 pr-4 pl-3 text-white rounded bg-primary-700 lg:bg-transparent lg:text-primary-700 lg:p-0 dark:text-white"
            activeClassName="bg-primary-600"
          >
            Home
          </Link>
          <NLink
            to="/contact"
            className="block font-semibold py-2 pr-4 pl-3 text-white rounded bg-primary-700 lg:bg-transparent lg:text-primary-700 lg:p-0 dark:text-white"
            activeClassName="bg-primary-600"
          >
            Contact
          </NLink>
          
          <CartIcon className="block font-semibold py-2 pr-4 pl-3 text-white rounded bg-primary-700 lg:bg-transparent lg:text-primary-700 lg:p-0 dark:text-white"
            activeClassName="bg-primary-600"></CartIcon>
        </NavigationMenu>

        <NavigationMobile>
          <ul
            id="mobile-menu" // Added id for accessibility
            className={`flex flex-col mt-4 font-medium lg:flex-row lg:space-x-8 lg:mt-0 transition-all duration-300 ${
              isMenuOpen ? "block" : "hidden"
            }`}
          >
            <li>
              <Link
                to="/"
                onClick={closeMenu}
                className="block font-semibold py-2 pr-4 pl-3 text-white rounded bg-primary-700 lg:bg-transparent lg:text-primary-700 lg:p-0 dark:text-white"
                aria-current="page"
              >
                Home
              </Link>
            </li>
            <li>
              <Link to="/contact" onClick={closeMenu} className="font-semibold">
                Contact
              </Link>
            </li>
            <li>
              <CartIcon />
            </li>
          </ul>
        </NavigationMobile>

        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search products..."
            className="px-4 py-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-white focus:ring-2 focus:ring-primary-400 transition-all"
          />
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-lg bg-primary-500 text-white hover:bg-primary-600 transition-colors group"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6 group-hover:rotate-180 transition-transform duration-300"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z"
              />
            </svg>
          </button>
        </div>
      </NavigationResonsive>

      {/* Hamburger Icon for Mobile */}
      <button
        onClick={toggleMenu}
        className="lg:hidden p-2 rounded-md text-primary-600 hover:text-primary-700 dark:text-white dark:hover:text-primary-400 transition-colors"
        aria-label={isMenuOpen ? "Close mobile menu" : "Open mobile menu"}
        aria-expanded={isMenuOpen}
        aria-controls="mobile-menu" // Linking button to menu
      >
        {isMenuOpen ? (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        ) : (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        )}
      </button>
    </NavigationContainer>
  );
}

export default Header;
