import React from "react";

const PriceTag = ({ price, discountedPrice}) => {

    const finalPrice = 
    price === discountedPrice ? price : (price - discountedPrice).toFixed(2);

    return (
        <p className="text-primary-600 font-bold">
            Price: {finalPrice} NOK
        </p>
    );
};

const PriceTagForSingleProduct = ({ price, discountedPrice}) => {

    const finalPrice = 
    price === discountedPrice ? price : (price - discountedPrice).toFixed(2);

    return (
        <p className="text-3xl font-bold">
            Price: {finalPrice} NOK
        </p>
    );
};
const PriceTagForCartPage = ({ price, discountedPrice}) => {

    const finalPrice = 
    price === discountedPrice ? price : (price - discountedPrice).toFixed(2);

    return (
        <td className="py-4">
            {finalPrice} NOK
        </td>
    );
};
const PriceTagTotalForCartPage = ({ price, discountedPrice, quantity }) => {
    const validPrice = !isNaN(price) ? price : 0;
    const validDiscountedPrice = !isNaN(discountedPrice) ? discountedPrice : 0;
    const validQuantity = !isNaN(quantity) && quantity > 0 ? quantity : 1; // ถ้าไม่ถูกต้องให้ใช้ค่าเริ่มต้นเป็น 1
  
    // คำนวณราคาสุดท้าย
    const finalPrice =
      validPrice === validDiscountedPrice
        ? validPrice * validQuantity
        : ((validPrice - validDiscountedPrice) * validQuantity).toFixed(2);
  
    return (
      <td className="py-4">
        {finalPrice} NOK
      </td>
    );
  };

export {PriceTag, PriceTagForSingleProduct, PriceTagForCartPage, PriceTagTotalForCartPage};
