import { Link } from "react-router-dom";
import { useCart } from "../../hooks/CartContext";
import { useMemo } from "react";

function CartIcon() {
  const { cart } = useCart();

  // Log cart to check if it's updating correctly
  console.log(cart); // See if this updates as expected

  // Calculate total items in the cart
  const totalItems = useMemo(() => {
    return cart.reduce((sum, item) => sum + item.quantity, 0);
  }, [cart]);

  // Memoized badge styles
  const badgeStyle = useMemo(() => ({
    position: "absolute",
    top: "-5px",
    right: "-5px",
    background: "#b91c1c",
    borderRadius: "50%",
    padding: "5px",
    color: "white",
    fontSize: "12px",
    minWidth: "20px",
    height: "20px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
  }), []);

  return (
    <Link
      to="/cart"
      style={{
        position: "relative",
        padding: "10px",
        fontWeight: "600",
        display: "flex",
        alignItems: "center",
      }}
      aria-label={`View cart, ${totalItems} items`}
    >
      🛒 Cart
      {totalItems > 0 && (
        <span style={badgeStyle} aria-live="polite">
          {totalItems}
        </span>
      )}
    </Link>
  );
}


export default CartIcon;
