              </ul>
            </div>
            <div className="mt-12">
              <h1 className="text-xl font-semibold text-black">Contact Us</h1>
              <form onSubmit={handleSubmit} className="mt-8 space-y-4">
                <div>
                  <input
                    type="text"
                    id="fullName"
                    name="fullName"
                    placeholder="Name"
                    value={formData.fullName}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
                    aria-label="Full Name"
                  />
                  {errors.fullName && <span className="error">{errors.fullName}</span>}
                </div>
                <div>
                  <input
                    type="text"
                    id="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
                    aria-label="Email"
                  />
                  {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    placeholder="Subject"
                    value={formData.subject}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
                    aria-label="Subject"
                  />
                  {errors.subject && <span className="error">{errors.subject}</span>}
                </div>
                <div>
                  <textarea
                    id="body"
                    name="body"
                    placeholder="Message"
                    value={formData.body}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    className="w-full rounded-md py-3 px-4 bg-gray-100 text-gray-800 text-sm outline-blue-500 focus:bg-transparent"
                    aria-label="Message"
                  />
                  {errors.body && <span className="error">{errors.body}</span>}
                </div>
                <div>
                  <button
                    type="submit"
                    disabled={!isFormValid || isSubmitting} // Disable button if submitting
                    className="w-full py-3 bg-blue-500 text-white font-semibold rounded-md disabled:bg-gray-400"
                  >
                    {isSubmitting ? "Sending..." : "Send Message"}
                  </button>
                </div>
                {successMessage && <p className="text-green-500">{successMessage}</p>}
              </form>
            </div>
          </div>
        </div>
      </ContactResponsive>
    </ContactContainer>
  );
};

export default ContactForm;
