import { Link } from "react-router-dom";
import { CartProvider } from "../../hooks/CartContext";
import CartIcon from "../../components/icon/CartIcon";
import {
  FooterContainer,
  FooterResonsive,
  FooterMenu,
} from "../ui/footer";

function Footer() {
  return (
    <FooterContainer>
      <FooterResonsive>
        <FooterMenu>
        <div>
                    <h3 class="text-lg font-semibold text-primary-800 dark:text-white mb-4">About Us</h3>
                    <p class="text-primary-700 dark:text-gray-300">Your premier destination for fashion and style.</p>
        </div>
          <div className="flex space-x-6">
            <Link to="/" className="hover:text-primary-500 transition">
              Home
            </Link>
            <Link to="/contact" className="hover:text-primary-500 transition">
              Contact
            </Link>
          </div>
          <CartProvider>
            <CartIcon />
          </CartProvider>
          <div className="text-sm">&copy; 2024 My Ecom Store</div>
        </FooterMenu>
      </FooterResonsive>
    </FooterContainer>
  );
}

export default Footer;
