import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useCart } from "../../hooks/CartContext";
import { PriceTagForSingleProduct } from "../../components/ui/priceTag";
import {
  SingleProductContainer,
  SingleProductResponsive,
} from "../../components/ui/singleProduct";

function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { addToCart } = useCart();
  const [addedToCart, setAddedToCart] = useState(false);

  // Async function to fetch the product data
  const fetchProduct = async () => {
    try {
      const res = await fetch(`https://v2.api.noroff.dev/online-shop/${id}`);
      const data = await res.json();
      setProduct(data);
    } catch (error) {
      console.error("Error fetching product data:", error);
    }
  };

  // Call the fetchProduct function when the component mounts
  useEffect(() => {
    fetchProduct();
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const handleAddToCart = () => {
    addToCart(product);
    setAddedToCart(true);

    setTimeout(() => {
      setAddedToCart(false);
    }, 2000);
  };

  return (
    <SingleProductContainer>
      <SingleProductResponsive>
        <div className="space-y-4">
          <div
            className="aspect-square bg-gray-100 dark:bg-gray-800 
          rounded-xl overflow-hidden"
          >
            <img
              src={product.data.image?.url || "/default-image.jpg"}
              alt={product.data.image?.alt || "Product Image"}
              className="w-full h-full object-cover"
            />
          </div>
        </div>

        <div className="space-y-6">
          <div className="flex items-start justify-between">
            <div>
              <h1 className="text-3xl font-bold mb-2">{product.data.title}</h1>
            </div>

            <PriceTagForSingleProduct
              price={product.data.price}
              discountedPrice={product.data.discountedPrice}
            />
          </div>
          <div className="text-lg font-semibold">
            {product.data.description}
          </div>
          <div className="flex gap-4">
            <button
              className="flex-1 bg-sky-800 text-white py-4 rounded-xl hover:bg-sky-800/90"
              onClick={handleAddToCart}
            >
              {addedToCart ? "Added!" : "Add to cart"}
            </button>
          </div>
        </div>
      </SingleProductResponsive>
      {/* <!-- Reviews Section --> */}
      <div className="mt-16">
        <div className="border-b border-gray-200 dark:border-gray-800">
          <div className="flex gap-8">
            <button className="px-4 py-2 text-primary border-b-2 border-primary">
              Reviews
            </button>
          </div>
        </div>
        <div className="mt-8 grid md:grid-cols-2 gap-8">
          <div className="space-y-6">
            <div className="flex items-start gap-4">
              <div className="flex-1">
                <div className="flex items-center justify-between mb-1">
                  <div>
                    {product.data.reviews.map((review) => (
                      <div key={review.id}>
                        <h4 className="font-medium">{review.username}</h4>
                        <p>{review.comment}</p>
                        <p className="text-gray-600 dark:text-gray-300">
                          {review.description}
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
          {/* Rating section */}
          <div className="space-y-6">
            <div className="flex items-center gap-4">
              <div>
                {product.data.reviews.map((rating) => (
                  <div key={rating.id}>
                    <div className="text-5xl font-bold text-primary">
                      {rating.rating}
                    </div>
                    <div className="flex-1">
                            <div className="flex items-center gap-2 mb-1">
                                <div className="flex-1 h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
                                    <div className="h-full bg-primary" style={{ width: "90%" }}></div>
                                </div>
                                <span className="text-sm text-gray-500 dark:text-gray-400">28</span>
                            </div>
                            <div className="flex items-center gap-2 mb-1">
                                <div className="flex-1 h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
                                    <div className="h-full bg-primary" style={"width: 70%"}></div>
                                </div>
                                <span className="text-sm text-gray-500 dark:text-gray-400">9</span>
                            </div>
                            <div className="flex items-center gap-2 mb-1">
                                <div className="flex-1 h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
                                    <div className="h-full bg-primary" style="width: 40%"></div>
                                </div>
                                <span className="text-sm text-gray-500 dark:text-gray-400">4</span>
                            </div>
                            <div className="flex items-center gap-2 mb-1">
                                <div className="flex-1 h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
                                    <div className="h-full bg-primary" style="width: 20%"></div>
                                </div>
                                <span className="text-sm text-gray-500 dark:text-gray-400">1</span>
                            </div>
                            <div className="flex items-center gap-2">
                                <div className="flex-1 h-2 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden">
                                    <div className="h-full bg-primary" style="width: 10%"></div>
                                </div>
                                <span className="text-sm text-gray-500 dark:text-gray-400">1</span>
                            </div>
                        </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </SingleProductContainer>
  );
}

export default ProductPage;
