import React, { useState } from 'react';
import {ContactContainer} from "../../components/ui/contactForm";

const ContactPage = () => {
  // State for form fields
  const [formData, setFormData] = useState({
    fullName: '',
    subject: '',
    email: '',
    body: '',
  });

  // State for form errors
  const [errors, setErrors] = useState({
    fullName: '',
    subject: '',
    email: '',
    body: '',
  });

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Validate form
  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    if (formData.fullName.length < 3) {
      formErrors.fullName = 'Full name must be at least 3 characters.';
      isValid = false;
    }
    if (formData.subject.length < 3) {
      formErrors.subject = 'Subject must be at least 3 characters.';
      isValid = false;
    }
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      formErrors.email = 'Please enter a valid email address.';
      isValid = false;
    }
    if (formData.body.length < 3) {
      formErrors.body = 'Body must be at least 3 characters.';
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // If the form is valid, log the data to the console
      console.log('Form submitted:', formData);
      // Clear the form (optional)
      setFormData({
        fullName: '',
        subject: '',
        email: '',
        body: '',
      });
    }
  };

  return (
    <ContactContainer>
      <div>
        <h1 className="text-gray-800 text-3xl font-extrabold">Let's Talk</h1>
        <div className="contact-page">
          <h1>Contact Us</h1>
          <p className="text-sm text-gray-500 mt-4">If you can’t find the information you need in our Help articles, and would like to get in touch with us, we’re here to help..</p>
          <div className="mt-12">
                    <h2 className="text-gray-800 text-base font-bold">Email</h2>
                    <ul className="mt-4">
                        <li className="flex items-center">
                            <div className="bg-[#e6e6e6cf] h-10 w-10 rounded-full flex items-center justify-center shrink-0">
                                <svg xmlns="http://www.w3.org/2000/svg" width="20px" height="20px" fill='#007bff'
                                    viewBox="0 0 479.058 479.058">
                                    <path
                                        d="M434.146 59.882H44.912C20.146 59.882 0 80.028 0 104.794v269.47c0 24.766 20.146 44.912 44.912 44.912h389.234c24.766 0 44.912-20.146 44.912-44.912v-269.47c0-24.766-20.146-44.912-44.912-44.912zm0 29.941c2.034 0 3.969.422 5.738 1.159L239.529 264.631 39.173 90.982a14.902 14.902 0 0 1 5.738-1.159zm0 299.411H44.912c-8.26 0-14.971-6.71-14.971-14.971V122.615l199.778 173.141c2.822 2.441 6.316 3.655 9.81 3.655s6.988-1.213 9.81-3.655l199.778-173.141v251.649c-.001 8.26-6.711 14.97-14.971 14.97z"
                                        data-original="#000000" />
                                </svg>
                            </div>
                            <a href="javascript:void(0)" className="text-[#007bff] text-sm ml-4">
                                <small className="block">Mail</small>
                                <strong>info@example.com</strong>
                            </a>
                        </li>
                    </ul>
                </div>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="fullName">Full Name</label>
              <input
                type="text"
                id="fullName"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
              />
              {errors.fullName && <p className="error">{errors.fullName}</p>}
            </div>

            <div className="form-group">
              <label htmlFor="subject">Subject</label>
              <input
                type="text"
                id="subject"
                name="subject"
                value={formData.subject}
                onChange={handleChange}
              />
              {errors.subject && <p className="error">{errors.subject}</p>}
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <p className="error">{errors.email}</p>}
            </div>

            <div className="form-group">
              <label htmlFor="body">Message Body</label>
              <textarea
                id="body"
                name="body"
                value={formData.body}
                onChange={handleChange}
              />
              {errors.body && <p className="error">{errors.body}</p>}
            </div>

            <button type="submit">Submit</button>
          </form>
        </div>
      </div>
    </ContactContainer>
  );
};

export default ContactPage;
