import { useCart } from "../../hooks/CartContext";
import {
  CheckoutBackground,
  CheckoutContainer,
  CheckoutResponsive,
} from "../../components/ui/checkout";

import { Button } from "../../components/ui/button";
import { PriceTagForCartPage } from "../../components/ui/priceTag";

function CartPage() {
  const { cart, removeFromCart, clearCart, updateCartQuantity } = useCart();

  const handleQuantityChange = (itemId, newQuantity) => {
    if (newQuantity < 1) return; 
    updateCartQuantity(itemId, newQuantity); 
  };

  console.log("this is product",cart);

  return (
    <CheckoutBackground>
      <CheckoutContainer>
        <h1 className="text-2xl font-semibold mb-4">Shopping Cart</h1>
        <CheckoutResponsive>
          <div className="md:w-3/4">
            <div className="bg-white rounded-lg shadow-md p-6 mb-4">
              <table className="w-full">
                <thead>
                  <tr>
                    <th className="text-left font-semibold">Product</th>
                    <th className="text-left font-semibold">Price</th>
                    <th className="text-left font-semibold">Quantity</th>
                    <th className="text-left font-semibold">Total</th>
                  </tr>
                </thead>
                <tbody>
                {cart?.length > 0 ? (
                    cart.map((item) => (
                      <tr key={item.id} className="border-t">
                        <td className="py-4">
                          <div className="flex items-center">
                            <img
                              src={item.data.image?.url}
                              alt={item.data.image?.alt}
                              className="h-16 w-16 mr-4"
                            />
                            <span className="font-semibold">{item.data.title}</span>
                          </div>
                        </td>
                        <PriceTagForCartPage price={item.data.price} discountedPrice={item.data.discountedPrice}></PriceTagForCartPage> 

                        <td className="py-4">
                          <div className="flex items-center">
                          <button
                              className="border rounded-md py-2 px-4 mr-2"
                              onClick={() =>
                                handleQuantityChange(item.id, item.quantity - 1)
                              }
                              disabled={item.quantity <= 1}
                            >
                              -
                            </button>
                              <span className="text-center w-8">1</span>
                            <button className="border rounded-md py-2 px-4 ml-2">+</button>
                          </div>
                        </td>
                        <td className="py-4">${item.price * item.quantity}</td>
                        <td className="py-4">
                          <Button
                            variant="destructive"
                            onClick={() => removeFromCart(item.id)}
                          >
                            Remove
                          </Button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center py-4">
                        Your cart is empty.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>

          <div className="md:w-1/4">
            {cart.length > 0 && (
              <Button variant="destructive" onClick={clearCart}>
                Clear Cart
              </Button>
            )}
          </div>
        </CheckoutResponsive>
      </CheckoutContainer>
    </CheckoutBackground>
  );
}

export default CartPage;
