import React, { useState } from "react";
import { ContactContainer } from "../../components/ui/contactForm";

const ContactPage = () => {
  // State for form fields
  const [formData, setFormData] = useState({
    fullName: "",
    subject: "",
    email: "",
    body: "",
  });

  // State for form errors
  const [errors, setErrors] = useState({
    fullName: "",
    subject: "",
    email: "",
    body: "",
  });

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Validate form
  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    if (formData.fullName.length < 3) {
      formErrors.fullName = "Full name must be at least 3 characters.";
      isValid = false;
    }
    if (formData.subject.length < 3) {
      formErrors.subject = "Subject must be at least 3 characters.";
      isValid = false;
    }
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      formErrors.email = "Please enter a valid email address.";
      isValid = false;
    }
    if (formData.body.length < 3) {
      formErrors.body = "Body must be at least 3 characters.";
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // If the form is valid, log the data to the console
      console.log("Form submitted:", formData);
      // Clear the form (optional)
      setFormData({
        fullName: "",
        subject: "",
        email: "",
        body: "",
      });
    }
  };

  return (
    <ContactContainer>
      <div>
        <h1 className="text-gray-800 text-3xl font-extrabold">Let's Talk</h1>
        <div className="contact-page">
          <h1>Contact Us</h1>
          <p className="text-sm text-gray-500 mt-4">
            If you can’t find the information you need in our Help articles, and
            would like to get in touch with us, we’re here to help..
          </p>
          <div className="mt-12">
            <h2 className="text-gray-800 text-base font-bold">Email</h2>
            <ul className="mt-4">
              <li className="flex items-center">
                <div className="bg-[#e6e6e6cf] h-10 w-10 rounded-full flex items-center justify-center shrink-0">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20px"
                    height="20px"
                    fill="#007bff"
                    viewBox="0 0 479.058 479.058"
                  >
                    <path
